<?php	require_once ("../lib/common/dao/VO.class.php");	class Category extends VO {		private $projectVO;		private $projectid;		private $categoryid;		private $parentid;		private $serialindex;		private $categoryname;		private $title;		private $link;		private $rssurl;		private $description;		private $useparenttags;		public function setProject($projectVO){			$this->projectVO=$projectVO;		}		public function getProject(){			return $this->projectVO;		}		public function setProjectid($projectid){			$this->projectid=$projectid;		}		public function getProjectid(){			return $this->projectid;		}		public function setCategoryid($categoryid){			$this->categoryid=$categoryid;		}		public function getCategoryid(){			return $this->categoryid;		}		public function setParentid($parentid){			$this->parentid=$parentid;		}		public function getParentid(){			return $this->parentid;		}		public function setSerialindex($serialindex){			$this->serialindex=$serialindex;		}		public function getSerialindex(){			return $this->serialindex;		}		public function setCategoryname($categoryname){			$this->categoryname=$categoryname;		}		public function getCategoryname(){			return $this->categoryname;		}		public function setTitle($title){			$this->title=$title;		}		public function getTitle(){			return $this->title;		}		public function setLink($link){			$this->link=$link;		}		public function getLink(){			return $this->link;		}		public function setRssurl($rssurl){			$this->rssurl=$rssurl;		}		public function getRssurl(){			return $this->rssurl;		}		public function setDescription($description){			$this->description=$description;		}		public function getDescription(){			return $this->description;		}		public function setUseparenttags($useparenttags){			$this->useparenttags=$useparenttags;		}		public function getUseparenttags(){			return $this->useparenttags;		}		public function __toString(){			return "projectid=".$this->projectid."(".$this->projectVO.")".", categoryid=".$this->categoryid.", parentid=".$this->parentid.", serialindex=".$this->serialindex.", categoryname=".$this->categoryname.", title=".$this->title.", link=".$this->link.", rssurl=".$this->rssurl.", description=".$this->description.", useparenttags=".$this->useparenttags;		}	}?>