<?php        require_once (dirname(__FILE__)."/../lib/common/dao/SearchDAO.class.php");	require_once (dirname(__FILE__)."/../lib/common/dao/DAO.class.php");	require_once (dirname(__FILE__)."/../lib/common/Manager.class.php");	require_once (dirname(__FILE__)."/./Organization.class.php");	/*	 * Manager class of ORGANIZATION table	 */	class OrganizationManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get a Organization instance by primary key		 * @param <int> $organizationid		 * @return <Organization> $vo		 */		public function getOrganizationByID($organizationid){			$conn = $this->getConnection();			$dao = new DAO();			$vo = new Organization();			$vo->setOrganizationid($organizationid);			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get a Organization instance by primary key		 * primary key value in $vo (value object) must not be null		 * @param <Organization> $vo		 * @return <Organization> $vo		 */		public function getOrganization($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get an array of organizationid=>Organization map		 * @param <Organization> $vo		 * @return Array<key<int>=>value<Organization>> $voMap		 */		public function getOrganizationMap($vo){			$conn = $this->getConnection();			$voMap = array();			$dao = new DAO();			$vos = $dao->query($vo);			for($i=0; $i<count($vos); $i++){				$curr_vo = $vos[$i];				$voMap[$curr_vo->getOrganizationid()] = $curr_vo;			}			$this->closeConnection($conn);			return $voMap;		}		/**		 * get an array of Organization instances		 * query condition is stored in value object		 * @param <Organization> $vo		 * @param <string> $orderby		 * @return Array<Organization>		 */		public function getOrganizations($vo, $orderby){			$conn = $this->getConnection();                        if($this->isScrollPageEnabled()){                                                                $dao = new DAO();                                $dao->enableScrollPage();                        } else {                                $dao = new DAO();                                $dao->disableScrollPage();                        }			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Organization instances by where clause		 * @param <Organization> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Organization>		 */		public function getOrganizationsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Organization instances by SQL		 * @param <Organization> $vo		 * @param <string> $sql		 * @return Array<Organization>		 */		public function getOrganizationsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Organization record to database		 * @param <Organization> $vo		 * @return <Organization> $vo - a Organization instance with primary key value		 */		public function addOrganization($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a Organization record in database		 * primary key value in $vo (value object) must not be null		 * @param <Organization> $vo		 * @return <int> affected row number (0 or 1)		 */		public function updateOrganization($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->update($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * update a batch of Organization records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateOrganizationBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a Organization record in database		 * primary key value in $vo (value object) must not be null		 * @param <Organization> $vo		 * @return <int> affected row number (0 or 1)		 */		public function deleteOrganization($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->delete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Organization records in database		 * @param <Organization> $vo		 * @return <int> affected row number		 */		public function deleteOrganizations($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Organization records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteOrganizationsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>