<?php	require_once ("../lib/common/dao/VO.class.php");	class Orgcontrol extends VO {		private $userVO;		private $organizationVO;		private $userid;		private $organizationid;		private $intyear;		private $semester;		public function setUser($userVO){			$this->userVO=$userVO;		}		public function getUser(){			return $this->userVO;		}		public function setOrganization($organizationVO){			$this->organizationVO=$organizationVO;		}		public function getOrganization(){			return $this->organizationVO;		}		public function setUserid($userid){			$this->userid=$userid;		}		public function getUserid(){			return $this->userid;		}		public function setOrganizationid($organizationid){			$this->organizationid=$organizationid;		}		public function getOrganizationid(){			return $this->organizationid;		}		public function setIntyear($intyear){			$this->intyear=$intyear;		}		public function getIntyear(){			return $this->intyear;		}		public function setSemester($semester){			$this->semester=$semester;		}		public function getSemester(){			return $this->semester;		}		public function __toString(){			return "userid=".$this->userid."(".$this->userVO.")".", organizationid=".$this->organizationid."(".$this->organizationVO.")".", intyear=".$this->intyear.", semester=".$this->semester;		}	}?>