<?php	require_once ("../lib/common/dao/VO.class.php");	class Project extends VO {		private $projectid;		private $parentid;		private $serialindex;		private $projectname;		private $projecturl;		private $description;		public function setProjectid($projectid){			$this->projectid=$projectid;		}		public function getProjectid(){			return $this->projectid;		}		public function setParentid($parentid){			$this->parentid=$parentid;		}		public function getParentid(){			return $this->parentid;		}		public function setSerialindex($serialindex){			$this->serialindex=$serialindex;		}		public function getSerialindex(){			return $this->serialindex;		}		public function setProjectname($projectname){			$this->projectname=$projectname;		}		public function getProjectname(){			return $this->projectname;		}		public function setProjecturl($projecturl){			$this->projecturl=$projecturl;		}		public function getProjecturl(){			return $this->projecturl;		}		public function setDescription($description){			$this->description=$description;		}		public function getDescription(){			return $this->description;		}		public function __toString(){			return "projectid=".$this->projectid.", parentid=".$this->parentid.", serialindex=".$this->serialindex.", projectname=".$this->projectname.", projecturl=".$this->projecturl.", description=".$this->description;		}	}?>