<?php	require_once ("../lib/common/dao/DAO.class.php");	require_once ("../lib/common/Manager.class.php");	require_once ("Project.class.php");	/*	 * Manager class of PROJECT table	 */	class ProjectManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get a Project instance by primary key		 * @param <int> $projectid		 * @return <Project> $vo		 */		public function getProjectByID($projectid){			$conn = $this->getConnection();			$dao = new DAO();			$vo = new Project();			$vo->setProjectid($projectid);			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get a Project instance by primary key		 * primary key value in $vo (value object) must not be null		 * @param <Project> $vo		 * @return <Project> $vo		 */		public function getProject($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get an array of projectid=>Project map		 * @param <Project> $vo		 * @return Array<key<int>=>value<Project>> $voMap		 */		public function getProjectMap($vo){			$conn = $this->getConnection();			$voMap = array();			$dao = new DAO();			$vos = $dao->query($vo);			for($i=0; $i<count($vos); $i++){				$curr_vo = $vos[$i];				$voMap[$curr_vo->getProjectid()] = $curr_vo;			}			$this->closeConnection($conn);			return $voMap;		}		/**		 * get an array of Project instances		 * query condition is stored in value object		 * @param <Project> $vo		 * @param <string> $orderby		 * @return Array<Project>		 */		public function getProjects($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Project instances by where clause		 * @param <Project> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Project>		 */		public function getProjectsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Project instances by SQL		 * @param <Project> $vo		 * @param <string> $sql		 * @return Array<Project>		 */		public function getProjectsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Project record to database		 * @param <Project> $vo		 * @return <Project> $vo - a Project instance with primary key value		 */		public function addProject($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a Project record in database		 * primary key value in $vo (value object) must not be null		 * @param <Project> $vo		 * @return <int> affected row number (0 or 1)		 */		public function updateProject($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->update($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * update a batch of Project records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateProjectBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a Project record in database		 * primary key value in $vo (value object) must not be null		 * @param <Project> $vo		 * @return <int> affected row number (0 or 1)		 */		public function deleteProject($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->delete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Project records in database		 * @param <Project> $vo		 * @return <int> affected row number		 */		public function deleteProjects($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Project records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteProjectsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>