<?php	require_once ("../lib/common/dao/DAO.class.php");	require_once ("../lib/common/Manager.class.php");	require_once ("Projectorg.class.php");	/*	 * Manager class of PROJECTORG table	 */	class ProjectorgManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get an array of Projectorg instances		 * query condition is stored in value object		 * @param <Projectorg> $vo		 * @param <string> $orderby		 * @return Array<Projectorg>		 */		public function getProjectorgs($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Projectorg instances by where clause		 * @param <Projectorg> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Projectorg>		 */		public function getProjectorgsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Projectorg instances by SQL		 * @param <Projectorg> $vo		 * @param <string> $sql		 * @return Array<Projectorg>		 */		public function getProjectorgsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Projectorg record to database		 * @param <Projectorg> $vo		 * @return <Projectorg> $vo		 */		public function addProjectorg($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Projectorg records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateProjectorgBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Projectorg records in database		 * @param <Projectorg> $vo		 * @return <int> affected row number		 */		public function deleteProjectorgs($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Projectorg records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteProjectorgsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>