<?php	require_once ("\../lib\\common\\dao\\DAO.class.php");	require_once ("\../lib\\common\\Manager.class.php");	require_once ("\\.\\Projectstat.class.php");	/*	 * Manager class of PROJECTSTAT table	 */	class ProjectstatManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get an array of Projectstat instances		 * query condition is stored in value object		 * @param <Projectstat> $vo		 * @param <string> $orderby		 * @return Array<Projectstat>		 */		public function getProjectstats($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Projectstat instances by where clause		 * @param <Projectstat> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Projectstat>		 */		public function getProjectstatsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Projectstat instances by SQL		 * @param <Projectstat> $vo		 * @param <string> $sql		 * @return Array<Projectstat>		 */		public function getProjectstatsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Projectstat record to database		 * @param <Projectstat> $vo		 * @return <Projectstat> $vo		 */		public function addProjectstat($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Projectstat records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateProjectstatBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Projectstat records in database		 * @param <Projectstat> $vo		 * @return <int> affected row number		 */		public function deleteProjectstats($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Projectstat records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteProjectstatsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>