<?php	require_once ("\../lib\\common\\dao\\VO.class.php");	class Rolecontrol extends VO {		private $userVO;		private $roleVO;		private $userid;		private $roleid;		public function setUser($userVO){			$this->userVO=$userVO;		}		public function getUser(){			return $this->userVO;		}		public function setRole($roleVO){			$this->roleVO=$roleVO;		}		public function getRole(){			return $this->roleVO;		}		public function setUserid($userid){			$this->userid=$userid;		}		public function getUserid(){			return $this->userid;		}		public function setRoleid($roleid){			$this->roleid=$roleid;		}		public function getRoleid(){			return $this->roleid;		}		public function __toString(){			return "userid=".$this->userid."(".$this->userVO.")".", roleid=".$this->roleid."(".$this->roleVO.")";		}	}?>