<?php	require_once ("\../lib\\common\\dao\\DAO.class.php");	require_once ("\../lib\\common\\Manager.class.php");	require_once ("\\.\\Rolecontrol.class.php");	/*	 * Manager class of ROLECONTROL table	 */	class RolecontrolManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get an array of Rolecontrol instances		 * query condition is stored in value object		 * @param <Rolecontrol> $vo		 * @param <string> $orderby		 * @return Array<Rolecontrol>		 */		public function getRolecontrols($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Rolecontrol instances by where clause		 * @param <Rolecontrol> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Rolecontrol>		 */		public function getRolecontrolsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Rolecontrol instances by SQL		 * @param <Rolecontrol> $vo		 * @param <string> $sql		 * @return Array<Rolecontrol>		 */		public function getRolecontrolsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Rolecontrol record to database		 * @param <Rolecontrol> $vo		 * @return <Rolecontrol> $vo		 */		public function addRolecontrol($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Rolecontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateRolecontrolBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Rolecontrol records in database		 * @param <Rolecontrol> $vo		 * @return <int> affected row number		 */		public function deleteRolecontrols($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Rolecontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteRolecontrolsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>