<?php	require_once ("\../lib\\common\\dao\\VO.class.php");	class Rolegroup extends VO {		private $rolegroupid;		private $parentid;		private $serialindex;		private $rolegroupname;		private $rolegrouptext;		private $description;		public function setRolegroupid($rolegroupid){			$this->rolegroupid=$rolegroupid;		}		public function getRolegroupid(){			return $this->rolegroupid;		}		public function setParentid($parentid){			$this->parentid=$parentid;		}		public function getParentid(){			return $this->parentid;		}		public function setSerialindex($serialindex){			$this->serialindex=$serialindex;		}		public function getSerialindex(){			return $this->serialindex;		}		public function setRolegroupname($rolegroupname){			$this->rolegroupname=$rolegroupname;		}		public function getRolegroupname(){			return $this->rolegroupname;		}		public function setRolegrouptext($rolegrouptext){			$this->rolegrouptext=$rolegrouptext;		}		public function getRolegrouptext(){			return $this->rolegrouptext;		}		public function setDescription($description){			$this->description=$description;		}		public function getDescription(){			return $this->description;		}		public function __toString(){			return "rolegroupid=".$this->rolegroupid.", parentid=".$this->parentid.", serialindex=".$this->serialindex.", rolegroupname=".$this->rolegroupname.", rolegrouptext=".$this->rolegrouptext.", description=".$this->description;		}	}?>