<?php	require_once ("\../lib\\common\\dao\\DAO.class.php");	require_once ("\../lib\\common\\Manager.class.php");	require_once ("\\.\\Timedimension.class.php");	/*	 * Manager class of TIMEDIMENSION table	 */	class TimedimensionManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get a Timedimension instance by primary key		 * @param <int> $timeid		 * @return <Timedimension> $vo		 */		public function getTimedimensionByID($timeid){			$conn = $this->getConnection();			$dao = new DAO();			$vo = new Timedimension();			$vo->setTimeid($timeid);			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get a Timedimension instance by primary key		 * primary key value in $vo (value object) must not be null		 * @param <Timedimension> $vo		 * @return <Timedimension> $vo		 */		public function getTimedimension($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get an array of timeid=>Timedimension map		 * @param <Timedimension> $vo		 * @return Array<key<int>=>value<Timedimension>> $voMap		 */		public function getTimedimensionMap($vo){			$conn = $this->getConnection();			$voMap = array();			$dao = new DAO();			$vos = $dao->query($vo);			for($i=0; $i<count($vos); $i++){				$curr_vo = $vos[$i];				$voMap[$curr_vo->getTimeid()] = $curr_vo;			}			$this->closeConnection($conn);			return $voMap;		}		/**		 * get an array of Timedimension instances		 * query condition is stored in value object		 * @param <Timedimension> $vo		 * @param <string> $orderby		 * @return Array<Timedimension>		 */		public function getTimedimensions($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Timedimension instances by where clause		 * @param <Timedimension> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Timedimension>		 */		public function getTimedimensionsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Timedimension instances by SQL		 * @param <Timedimension> $vo		 * @param <string> $sql		 * @return Array<Timedimension>		 */		public function getTimedimensionsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Timedimension record to database		 * @param <Timedimension> $vo		 * @return <Timedimension> $vo - a Timedimension instance with primary key value		 */		public function addTimedimension($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a Timedimension record in database		 * primary key value in $vo (value object) must not be null		 * @param <Timedimension> $vo		 * @return <int> affected row number (0 or 1)		 */		public function updateTimedimension($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->update($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * update a batch of Timedimension records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateTimedimensionBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a Timedimension record in database		 * primary key value in $vo (value object) must not be null		 * @param <Timedimension> $vo		 * @return <int> affected row number (0 or 1)		 */		public function deleteTimedimension($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->delete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Timedimension records in database		 * @param <Timedimension> $vo		 * @return <int> affected row number		 */		public function deleteTimedimensions($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Timedimension records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteTimedimensionsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>