<?php	require_once ("../lib/common/dao/DAO.class.php");	require_once ("../lib/common/Manager.class.php");	require_once ("Category.class.php");	/*	 * Manager class of CATEGORY table	 */	class CategoryManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get a Category instance by primary key		 * @param <int> $categoryid		 * @return <Category> $vo		 */		public function getCategoryByID($categoryid){			$conn = $this->getConnection();			$dao = new DAO();			$vo = new Category();			$vo->setCategoryid($categoryid);			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get a Category instance by primary key		 * primary key value in $vo (value object) must not be null		 * @param <Category> $vo		 * @return <Category> $vo		 */		public function getCategory($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get an array of categoryid=>Category map		 * @param <Category> $vo		 * @return Array<key<int>=>value<Category>> $voMap		 */		public function getCategoryMap($vo){			$conn = $this->getConnection();			$voMap = array();			$dao = new DAO();			$vos = $dao->query($vo);			for($i=0; $i<count($vos); $i++){				$curr_vo = $vos[$i];				$voMap[$curr_vo->getCategoryid()] = $curr_vo;			}			$this->closeConnection($conn);			return $voMap;		}		/**		 * get an array of Category instances		 * query condition is stored in value object		 * @param <Category> $vo		 * @param <string> $orderby		 * @return Array<Category>		 */		public function getCategorys($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Category instances by where clause		 * @param <Category> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Category>		 */		public function getCategorysByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Category instances by SQL		 * @param <Category> $vo		 * @param <string> $sql		 * @return Array<Category>		 */		public function getCategorysBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Category record to database		 * @param <Category> $vo		 * @return <Category> $vo - a Category instance with primary key value		 */		public function addCategory($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a Category record in database		 * primary key value in $vo (value object) must not be null		 * @param <Category> $vo		 * @return <int> affected row number (0 or 1)		 */		public function updateCategory($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->update($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * update a batch of Category records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateCategoryBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a Category record in database		 * primary key value in $vo (value object) must not be null		 * @param <Category> $vo		 * @return <int> affected row number (0 or 1)		 */		public function deleteCategory($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->delete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Category records in database		 * @param <Category> $vo		 * @return <int> affected row number		 */		public function deleteCategorys($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Category records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteCategorysBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>