<?php	require_once ("../lib/common/dao/DAO.class.php");	require_once ("../lib/common/Manager.class.php");	require_once ("User.class.php");	/*	 * Manager class of USER table	 */	class UserManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get a User instance by primary key		 * @param <int> $userid		 * @return <User> $vo		 */		public function getUserByID($userid){			$conn = $this->getConnection();			$dao = new DAO();			$vo = new User();			$vo->setUserid($userid);			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get a User instance by primary key		 * primary key value in $vo (value object) must not be null		 * @param <User> $vo		 * @return <User> $vo		 */		public function getUser($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get an array of userid=>User map		 * @param <User> $vo		 * @return Array<key<int>=>value<User>> $voMap		 */		public function getUserMap($vo){			$conn = $this->getConnection();			$voMap = array();			$dao = new DAO();			$vos = $dao->query($vo);			for($i=0; $i<count($vos); $i++){				$curr_vo = $vos[$i];				$voMap[$curr_vo->getUserid()] = $curr_vo;			}			$this->closeConnection($conn);			return $voMap;		}		/**		 * get an array of User instances		 * query condition is stored in value object		 * @param <User> $vo		 * @param <string> $orderby		 * @return Array<User>		 */		public function getUsers($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of User instances by where clause		 * @param <User> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<User>		 */		public function getUsersByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of User instances by SQL		 * @param <User> $vo		 * @param <string> $sql		 * @return Array<User>		 */		public function getUsersBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a User record to database		 * @param <User> $vo		 * @return <User> $vo - a User instance with primary key value		 */		public function addUser($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a User record in database		 * primary key value in $vo (value object) must not be null		 * @param <User> $vo		 * @return <int> affected row number (0 or 1)		 */		public function updateUser($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->update($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * update a batch of User records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateUserBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a User record in database		 * primary key value in $vo (value object) must not be null		 * @param <User> $vo		 * @return <int> affected row number (0 or 1)		 */		public function deleteUser($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->delete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of User records in database		 * @param <User> $vo		 * @return <int> affected row number		 */		public function deleteUsers($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of User records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteUsersBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>