<?php	require_once ("../lib/common/dao/DAO.class.php");	require_once ("../lib/common/Manager.class.php");	require_once ("Cell.class.php");	/*	 * Manager class of CELL table	 */	class CellManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get an array of Cell instances		 * query condition is stored in value object		 * @param <Cell> $vo		 * @param <string> $orderby		 * @return Array<Cell>		 */		public function getCells($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Cell instances by where clause		 * @param <Cell> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Cell>		 */		public function getCellsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Cell instances by SQL		 * @param <Cell> $vo		 * @param <string> $sql		 * @return Array<Cell>		 */		public function getCellsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Cell record to database		 * @param <Cell> $vo		 * @return <Cell> $vo		 */		public function addCell($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Cell records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateCellBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Cell records in database		 * @param <Cell> $vo		 * @return <int> affected row number		 */		public function deleteCells($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Cell records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteCellsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>