<?php	require_once ("../lib/common/dao/DAO.class.php");	require_once ("../lib/common/Manager.class.php");	require_once ("Configinstructor.class.php");	/*	 * Manager class of CONFIGINSTRUCTOR table	 */	class ConfiginstructorManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get an array of Configinstructor instances		 * query condition is stored in value object		 * @param <Configinstructor> $vo		 * @param <string> $orderby		 * @return Array<Configinstructor>		 */		public function getConfiginstructors($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Configinstructor instances by where clause		 * @param <Configinstructor> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Configinstructor>		 */		public function getConfiginstructorsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Configinstructor instances by SQL		 * @param <Configinstructor> $vo		 * @param <string> $sql		 * @return Array<Configinstructor>		 */		public function getConfiginstructorsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Configinstructor record to database		 * @param <Configinstructor> $vo		 * @return <Configinstructor> $vo		 */		public function addConfiginstructor($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Configinstructor records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateConfiginstructorBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Configinstructor records in database		 * @param <Configinstructor> $vo		 * @return <int> affected row number		 */		public function deleteConfiginstructors($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Configinstructor records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteConfiginstructorsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>