<?php	require_once ("../lib/common/dao/DAO.class.php");	require_once ("../lib/common/Manager.class.php");	require_once ("Configuration.class.php");	/*	 * Manager class of CONFIGURATION table	 */	class ConfigurationManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get a Configuration instance by primary key		 * @param <int> $configurationid		 * @return <Configuration> $vo		 */		public function getConfigurationByID($configurationid){			$conn = $this->getConnection();			$dao = new DAO();			$vo = new Configuration();			$vo->setConfigurationid($configurationid);			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get a Configuration instance by primary key		 * primary key value in $vo (value object) must not be null		 * @param <Configuration> $vo		 * @return <Configuration> $vo		 */		public function getConfiguration($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get an array of configurationid=>Configuration map		 * @param <Configuration> $vo		 * @return Array<key<int>=>value<Configuration>> $voMap		 */		public function getConfigurationMap($vo){			$conn = $this->getConnection();			$voMap = array();			$dao = new DAO();			$vos = $dao->query($vo);			for($i=0; $i<count($vos); $i++){				$curr_vo = $vos[$i];				$voMap[$curr_vo->getConfigurationid()] = $curr_vo;			}			$this->closeConnection($conn);			return $voMap;		}		/**		 * get an array of Configuration instances		 * query condition is stored in value object		 * @param <Configuration> $vo		 * @param <string> $orderby		 * @return Array<Configuration>		 */		public function getConfigurations($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Configuration instances by where clause		 * @param <Configuration> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Configuration>		 */		public function getConfigurationsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Configuration instances by SQL		 * @param <Configuration> $vo		 * @param <string> $sql		 * @return Array<Configuration>		 */		public function getConfigurationsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Configuration record to database		 * @param <Configuration> $vo		 * @return <Configuration> $vo - a Configuration instance with primary key value		 */		public function addConfiguration($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a Configuration record in database		 * primary key value in $vo (value object) must not be null		 * @param <Configuration> $vo		 * @return <int> affected row number (0 or 1)		 */		public function updateConfiguration($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->update($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * update a batch of Configuration records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateConfigurationBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a Configuration record in database		 * primary key value in $vo (value object) must not be null		 * @param <Configuration> $vo		 * @return <int> affected row number (0 or 1)		 */		public function deleteConfiguration($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->delete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Configuration records in database		 * @param <Configuration> $vo		 * @return <int> affected row number		 */		public function deleteConfigurations($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Configuration records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteConfigurationsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>