<?php	require_once ("../lib/common/dao/VO.class.php");	class Domain extends VO {		private $domainid;		private $parentid;		private $serialindex;		private $domainname;		private $domaintext;		public function setDomainid($domainid){			$this->domainid=$domainid;		}		public function getDomainid(){			return $this->domainid;		}		public function setParentid($parentid){			$this->parentid=$parentid;		}		public function getParentid(){			return $this->parentid;		}		public function setSerialindex($serialindex){			$this->serialindex=$serialindex;		}		public function getSerialindex(){			return $this->serialindex;		}		public function setDomainname($domainname){			$this->domainname=$domainname;		}		public function getDomainname(){			return $this->domainname;		}		public function setDomaintext($domaintext){			$this->domaintext=$domaintext;		}		public function getDomaintext(){			return $this->domaintext;		}		public function __toString(){			return "domainid=".$this->domainid.", parentid=".$this->parentid.", serialindex=".$this->serialindex.", domainname=".$this->domainname.", domaintext=".$this->domaintext;		}	}?>