<?php	require_once ("../lib/common/dao/DAO.class.php");	require_once ("../lib/common/Manager.class.php");	require_once ("Domaincontrol.class.php");	/*	 * Manager class of DOMAINCONTROL table	 */	class DomaincontrolManager extends Manager {		/**		 * get an array of Domaincontrol instances		 * query condition is stored in value object		 * @param <Domaincontrol> $vo		 * @param <string> $orderby		 * @return Array<Domaincontrol>		 */		public function getDomaincontrols($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Domaincontrol instances by where clause		 * @param <Domaincontrol> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Domaincontrol>		 */		public function getDomaincontrolsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Domaincontrol instances by SQL		 * @param <Domaincontrol> $vo		 * @param <string> $sql		 * @return Array<Domaincontrol>		 */		public function getDomaincontrolsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Domaincontrol record to database		 * @param <Domaincontrol> $vo		 * @return <Domaincontrol> $vo		 */		public function addDomaincontrol($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Domaincontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateDomaincontrolBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Domaincontrol records in database		 * @param <Domaincontrol> $vo		 * @return <int> affected row number		 */		public function deleteDomaincontrols($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Domaincontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteDomaincontrols($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>