<?php	require_once ("../lib/common/dao/VO.class.php");	class Menugroup extends VO {		private $menugroupid;		private $parentid;		private $serialindex;		private $menugroupname;		private $menugrouptext;		private $description;		public function setMenugroupid($menugroupid){			$this->menugroupid=$menugroupid;		}		public function getMenugroupid(){			return $this->menugroupid;		}		public function setParentid($parentid){			$this->parentid=$parentid;		}		public function getParentid(){			return $this->parentid;		}		public function setSerialindex($serialindex){			$this->serialindex=$serialindex;		}		public function getSerialindex(){			return $this->serialindex;		}		public function setMenugroupname($menugroupname){			$this->menugroupname=$menugroupname;		}		public function getMenugroupname(){			return $this->menugroupname;		}		public function setMenugrouptext($menugrouptext){			$this->menugrouptext=$menugrouptext;		}		public function getMenugrouptext(){			return $this->menugrouptext;		}		public function setDescription($description){			$this->description=$description;		}		public function getDescription(){			return $this->description;		}		public function __toString(){			return "menugroupid=".$this->menugroupid.", parentid=".$this->parentid.", serialindex=".$this->serialindex.", menugroupname=".$this->menugroupname.", menugrouptext=".$this->menugrouptext.", description=".$this->description;		}	}?>