<?php	require_once ("\../lib\\common\\dao\\DAO.class.php");	require_once ("\../lib\\common\\Manager.class.php");	require_once ("\\.\\Module.class.php");	/*	 * Manager class of MODULE table	 */	class ModuleManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get a Module instance by primary key		 * @param <int> $moduleid		 * @return <Module> $vo		 */		public function getModuleByID($moduleid){			$conn = $this->getConnection();			$dao = new DAO();			$vo = new Module();			$vo->setModuleid($moduleid);			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get a Module instance by primary key		 * primary key value in $vo (value object) must not be null		 * @param <Module> $vo		 * @return <Module> $vo		 */		public function getModule($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get an array of moduleid=>Module map		 * @param <Module> $vo		 * @return Array<key<int>=>value<Module>> $voMap		 */		public function getModuleMap($vo){			$conn = $this->getConnection();			$voMap = array();			$dao = new DAO();			$vos = $dao->query($vo);			for($i=0; $i<count($vos); $i++){				$curr_vo = $vos[$i];				$voMap[$curr_vo->getModuleid()] = $curr_vo;			}			$this->closeConnection($conn);			return $voMap;		}		/**		 * get an array of Module instances		 * query condition is stored in value object		 * @param <Module> $vo		 * @param <string> $orderby		 * @return Array<Module>		 */		public function getModules($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Module instances by where clause		 * @param <Module> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Module>		 */		public function getModulesByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Module instances by SQL		 * @param <Module> $vo		 * @param <string> $sql		 * @return Array<Module>		 */		public function getModulesBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Module record to database		 * @param <Module> $vo		 * @return <Module> $vo - a Module instance with primary key value		 */		public function addModule($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a Module record in database		 * primary key value in $vo (value object) must not be null		 * @param <Module> $vo		 * @return <int> affected row number (0 or 1)		 */		public function updateModule($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->update($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * update a batch of Module records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateModuleBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a Module record in database		 * primary key value in $vo (value object) must not be null		 * @param <Module> $vo		 * @return <int> affected row number (0 or 1)		 */		public function deleteModule($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->delete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Module records in database		 * @param <Module> $vo		 * @return <int> affected row number		 */		public function deleteModules($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Module records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteModulesBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>