<?php	require_once ("\../lib\\common\\dao\\DAO.class.php");	require_once ("\../lib\\common\\Manager.class.php");	require_once ("\\.\\Modulemenucontrol.class.php");	/*	 * Manager class of MODULEMENUCONTROL table	 */	class ModulemenucontrolManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get an array of Modulemenucontrol instances		 * query condition is stored in value object		 * @param <Modulemenucontrol> $vo		 * @param <string> $orderby		 * @return Array<Modulemenucontrol>		 */		public function getModulemenucontrols($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Modulemenucontrol instances by where clause		 * @param <Modulemenucontrol> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Modulemenucontrol>		 */		public function getModulemenucontrolsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Modulemenucontrol instances by SQL		 * @param <Modulemenucontrol> $vo		 * @param <string> $sql		 * @return Array<Modulemenucontrol>		 */		public function getModulemenucontrolsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Modulemenucontrol record to database		 * @param <Modulemenucontrol> $vo		 * @return <Modulemenucontrol> $vo		 */		public function addModulemenucontrol($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Modulemenucontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateModulemenucontrolBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Modulemenucontrol records in database		 * @param <Modulemenucontrol> $vo		 * @return <int> affected row number		 */		public function deleteModulemenucontrols($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Modulemenucontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteModulemenucontrolsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>