<?php	require_once (dirname(__FILE__)."/../lib/common/dao/VO.class.php");	class Organization extends VO {		private $organizationid;		private $parentid;		private $serialindex;		private $orgname;		private $orgtext;		private $description;		public function setOrganizationid($organizationid){			$this->organizationid=$organizationid;		}		public function getOrganizationid(){			return $this->organizationid;		}		public function setParentid($parentid){			$this->parentid=$parentid;		}		public function getParentid(){			return $this->parentid;		}		public function setSerialindex($serialindex){			$this->serialindex=$serialindex;		}		public function getSerialindex(){			return $this->serialindex;		}		public function setOrgname($orgname){			$this->orgname=$orgname;		}		public function getOrgname(){			return $this->orgname;		}		public function setOrgtext($orgtext){			$this->orgtext=$orgtext;		}		public function getOrgtext(){			return $this->orgtext;		}		public function setDescription($description){			$this->description=$description;		}		public function getDescription(){			return $this->description;		}		public function __toString(){			return "organizationid=".$this->organizationid.", parentid=".$this->parentid.", serialindex=".$this->serialindex.", orgname=".$this->orgname.", orgtext=".$this->orgtext.", description=".$this->description;		}	}?>