<?php        require_once (dirname(__FILE__)."/../lib/common/dao/SearchDAO.class.php");	require_once (dirname(__FILE__)."/../lib/common/dao/DAO.class.php");	require_once (dirname(__FILE__)."/../lib/common/Manager.class.php");	require_once (dirname(__FILE__)."/./Orgcontrol.class.php");	/*	 * Manager class of ORGCONTROL table	 */	class OrgcontrolManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get an array of Orgcontrol instances		 * query condition is stored in value object		 * @param <Orgcontrol> $vo		 * @param <string> $orderby		 * @return Array<Orgcontrol>		 */		public function getOrgcontrols($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Orgcontrol instances by where clause		 * @param <Orgcontrol> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Orgcontrol>		 */		public function getOrgcontrolsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Orgcontrol instances by SQL		 * @param <Orgcontrol> $vo		 * @param <string> $sql		 * @return Array<Orgcontrol>		 */		public function getOrgcontrolsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Orgcontrol record to database		 * @param <Orgcontrol> $vo		 * @return <Orgcontrol> $vo		 */		public function addOrgcontrol($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Orgcontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateOrgcontrolBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Orgcontrol records in database		 * @param <Orgcontrol> $vo		 * @return <int> affected row number		 */		public function deleteOrgcontrols($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Orgcontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteOrgcontrolsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>