<?php	require_once ("\../lib\\common\\dao\\VO.class.php");	class Role extends VO {		private $rolegroupVO;		private $roleid;		private $rolegroupid;		private $rolename;		private $serialindex;		private $roletext;		private $description;		public function setRolegroup($rolegroupVO){			$this->rolegroupVO=$rolegroupVO;		}		public function getRolegroup(){			return $this->rolegroupVO;		}		public function setRoleid($roleid){			$this->roleid=$roleid;		}		public function getRoleid(){			return $this->roleid;		}		public function setRolegroupid($rolegroupid){			$this->rolegroupid=$rolegroupid;		}		public function getRolegroupid(){			return $this->rolegroupid;		}		public function setRolename($rolename){			$this->rolename=$rolename;		}		public function getRolename(){			return $this->rolename;		}		public function setSerialindex($serialindex){			$this->serialindex=$serialindex;		}		public function getSerialindex(){			return $this->serialindex;		}		public function setRoletext($roletext){			$this->roletext=$roletext;		}		public function getRoletext(){			return $this->roletext;		}		public function setDescription($description){			$this->description=$description;		}		public function getDescription(){			return $this->description;		}		public function __toString(){			return "roleid=".$this->roleid.", rolegroupid=".$this->rolegroupid."(".$this->rolegroupVO.")".", rolename=".$this->rolename.", serialindex=".$this->serialindex.", roletext=".$this->roletext.", description=".$this->description;		}	}?>