<?php	require_once ("\../lib\\common\\dao\\DAO.class.php");	require_once ("\../lib\\common\\Manager.class.php");	require_once ("\\.\\Rolegroup.class.php");	/*	 * Manager class of ROLEGROUP table	 */	class RolegroupManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get a Rolegroup instance by primary key		 * @param <int> $rolegroupid		 * @return <Rolegroup> $vo		 */		public function getRolegroupByID($rolegroupid){			$conn = $this->getConnection();			$dao = new DAO();			$vo = new Rolegroup();			$vo->setRolegroupid($rolegroupid);			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get a Rolegroup instance by primary key		 * primary key value in $vo (value object) must not be null		 * @param <Rolegroup> $vo		 * @return <Rolegroup> $vo		 */		public function getRolegroup($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get an array of rolegroupid=>Rolegroup map		 * @param <Rolegroup> $vo		 * @return Array<key<int>=>value<Rolegroup>> $voMap		 */		public function getRolegroupMap($vo){			$conn = $this->getConnection();			$voMap = array();			$dao = new DAO();			$vos = $dao->query($vo);			for($i=0; $i<count($vos); $i++){				$curr_vo = $vos[$i];				$voMap[$curr_vo->getRolegroupid()] = $curr_vo;			}			$this->closeConnection($conn);			return $voMap;		}		/**		 * get an array of Rolegroup instances		 * query condition is stored in value object		 * @param <Rolegroup> $vo		 * @param <string> $orderby		 * @return Array<Rolegroup>		 */		public function getRolegroups($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Rolegroup instances by where clause		 * @param <Rolegroup> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Rolegroup>		 */		public function getRolegroupsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Rolegroup instances by SQL		 * @param <Rolegroup> $vo		 * @param <string> $sql		 * @return Array<Rolegroup>		 */		public function getRolegroupsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Rolegroup record to database		 * @param <Rolegroup> $vo		 * @return <Rolegroup> $vo - a Rolegroup instance with primary key value		 */		public function addRolegroup($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a Rolegroup record in database		 * primary key value in $vo (value object) must not be null		 * @param <Rolegroup> $vo		 * @return <int> affected row number (0 or 1)		 */		public function updateRolegroup($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->update($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * update a batch of Rolegroup records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateRolegroupBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a Rolegroup record in database		 * primary key value in $vo (value object) must not be null		 * @param <Rolegroup> $vo		 * @return <int> affected row number (0 or 1)		 */		public function deleteRolegroup($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->delete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Rolegroup records in database		 * @param <Rolegroup> $vo		 * @return <int> affected row number		 */		public function deleteRolegroups($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Rolegroup records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteRolegroupsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>