<?php	require_once ("\../lib\\common\\dao\\VO.class.php");	class Rolemenucontrol extends VO {		private $roleVO;		private $menuVO;		private $roleid;		private $menuid;		public function setRole($roleVO){			$this->roleVO=$roleVO;		}		public function getRole(){			return $this->roleVO;		}		public function setMenu($menuVO){			$this->menuVO=$menuVO;		}		public function getMenu(){			return $this->menuVO;		}		public function setRoleid($roleid){			$this->roleid=$roleid;		}		public function getRoleid(){			return $this->roleid;		}		public function setMenuid($menuid){			$this->menuid=$menuid;		}		public function getMenuid(){			return $this->menuid;		}		public function __toString(){			return "roleid=".$this->roleid."(".$this->roleVO.")".", menuid=".$this->menuid."(".$this->menuVO.")";		}	}?>