<?php	require_once ("\../lib\\common\\dao\\DAO.class.php");	require_once ("\../lib\\common\\Manager.class.php");	require_once ("\\.\\Rolemenucontrol.class.php");	/*	 * Manager class of ROLEMENUCONTROL table	 */	class RolemenucontrolManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get an array of Rolemenucontrol instances		 * query condition is stored in value object		 * @param <Rolemenucontrol> $vo		 * @param <string> $orderby		 * @return Array<Rolemenucontrol>		 */		public function getRolemenucontrols($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Rolemenucontrol instances by where clause		 * @param <Rolemenucontrol> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Rolemenucontrol>		 */		public function getRolemenucontrolsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Rolemenucontrol instances by SQL		 * @param <Rolemenucontrol> $vo		 * @param <string> $sql		 * @return Array<Rolemenucontrol>		 */		public function getRolemenucontrolsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Rolemenucontrol record to database		 * @param <Rolemenucontrol> $vo		 * @return <Rolemenucontrol> $vo		 */		public function addRolemenucontrol($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Rolemenucontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateRolemenucontrolBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Rolemenucontrol records in database		 * @param <Rolemenucontrol> $vo		 * @return <int> affected row number		 */		public function deleteRolemenucontrols($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Rolemenucontrol records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteRolemenucontrolsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>