<?php	require_once ("\../lib\\common\\dao\\VO.class.php");	class Tag extends VO {		private $categoryVO;		private $tagid;		private $categoryid;		private $serialindex;		private $tagname;		private $tagtext;		private $description;		public function setCategory($categoryVO){			$this->categoryVO=$categoryVO;		}		public function getCategory(){			return $this->categoryVO;		}		public function setTagid($tagid){			$this->tagid=$tagid;		}		public function getTagid(){			return $this->tagid;		}		public function setCategoryid($categoryid){			$this->categoryid=$categoryid;		}		public function getCategoryid(){			return $this->categoryid;		}		public function setSerialindex($serialindex){			$this->serialindex=$serialindex;		}		public function getSerialindex(){			return $this->serialindex;		}		public function setTagname($tagname){			$this->tagname=$tagname;		}		public function getTagname(){			return $this->tagname;		}		public function setTagtext($tagtext){			$this->tagtext=$tagtext;		}		public function getTagtext(){			return $this->tagtext;		}		public function setDescription($description){			$this->description=$description;		}		public function getDescription(){			return $this->description;		}		public function __toString(){			return "tagid=".$this->tagid.", categoryid=".$this->categoryid."(".$this->categoryVO.")".", serialindex=".$this->serialindex.", tagname=".$this->tagname.", tagtext=".$this->tagtext.", description=".$this->description;		}	}?>