<?php	require_once ("\../lib\\common\\dao\\DAO.class.php");	require_once ("\../lib\\common\\Manager.class.php");	require_once ("\\.\\Tag.class.php");	/*	 * Manager class of TAG table	 */	class TagManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get a Tag instance by primary key		 * @param <int> $tagid		 * @return <Tag> $vo		 */		public function getTagByID($tagid){			$conn = $this->getConnection();			$dao = new DAO();			$vo = new Tag();			$vo->setTagid($tagid);			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get a Tag instance by primary key		 * primary key value in $vo (value object) must not be null		 * @param <Tag> $vo		 * @return <Tag> $vo		 */		public function getTag($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->queryByPK($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * get an array of tagid=>Tag map		 * @param <Tag> $vo		 * @return Array<key<int>=>value<Tag>> $voMap		 */		public function getTagMap($vo){			$conn = $this->getConnection();			$voMap = array();			$dao = new DAO();			$vos = $dao->query($vo);			for($i=0; $i<count($vos); $i++){				$curr_vo = $vos[$i];				$voMap[$curr_vo->getTagid()] = $curr_vo;			}			$this->closeConnection($conn);			return $voMap;		}		/**		 * get an array of Tag instances		 * query condition is stored in value object		 * @param <Tag> $vo		 * @param <string> $orderby		 * @return Array<Tag>		 */		public function getTags($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Tag instances by where clause		 * @param <Tag> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Tag>		 */		public function getTagsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Tag instances by SQL		 * @param <Tag> $vo		 * @param <string> $sql		 * @return Array<Tag>		 */		public function getTagsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Tag record to database		 * @param <Tag> $vo		 * @return <Tag> $vo - a Tag instance with primary key value		 */		public function addTag($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a Tag record in database		 * primary key value in $vo (value object) must not be null		 * @param <Tag> $vo		 * @return <int> affected row number (0 or 1)		 */		public function updateTag($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->update($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * update a batch of Tag records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateTagBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a Tag record in database		 * primary key value in $vo (value object) must not be null		 * @param <Tag> $vo		 * @return <int> affected row number (0 or 1)		 */		public function deleteTag($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->delete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Tag records in database		 * @param <Tag> $vo		 * @return <int> affected row number		 */		public function deleteTags($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Tag records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteTagsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>