<?php	require_once ("../lib/common/dao/VO.class.php");	class Unit extends VO {		private $categoryVO;		private $categoryid;		private $unitid;		private $serialindex;		private $unitname;		private $unittext;		private $description;		public function setCategory($categoryVO){			$this->categoryVO=$categoryVO;		}		public function getCategory(){			return $this->categoryVO;		}		public function setCategoryid($categoryid){			$this->categoryid=$categoryid;		}		public function getCategoryid(){			return $this->categoryid;		}		public function setUnitid($unitid){			$this->unitid=$unitid;		}		public function getUnitid(){			return $this->unitid;		}		public function setSerialindex($serialindex){			$this->serialindex=$serialindex;		}		public function getSerialindex(){			return $this->serialindex;		}		public function setUnitname($unitname){			$this->unitname=$unitname;		}		public function getUnitname(){			return $this->unitname;		}		public function setUnittext($unittext){			$this->unittext=$unittext;		}		public function getUnittext(){			return $this->unittext;		}		public function setDescription($description){			$this->description=$description;		}		public function getDescription(){			return $this->description;		}		public function __toString(){			return "categoryid=".$this->categoryid."(".$this->categoryVO.")".", unitid=".$this->unitid.", serialindex=".$this->serialindex.", unitname=".$this->unitname.", unittext=".$this->unittext.", description=".$this->description;		}	}?>