<?php	require_once ("\../lib\\common\\dao\\DAO.class.php");	require_once ("\../lib\\common\\Manager.class.php");	require_once ("\\.\\Userstat.class.php");	/*	 * Manager class of USERSTAT table	 */	class UserstatManager extends Manager {		//hold a single instance of the manager class		protected static $manager;		/**		 * singleton pattern method		 * get the single instance of the manager class		 * @return <OrganizationManager>		 */		public static function getInstance(){			if (!isset(self::$manager)) {				$c = __CLASS__;				self::$manager = new $c;			}			return self::$manager;		}		/**		 * get an array of Userstat instances		 * query condition is stored in value object		 * @param <Userstat> $vo		 * @param <string> $orderby		 * @return Array<Userstat>		 */		public function getUserstats($vo, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->query($vo, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Userstat instances by where clause		 * @param <Userstat> $vo		 * @param <string> $orderby		 * @param <string> $where		 * @return Array<Userstat>		 */		public function getUserstatsByWhere($vo, $where, $orderby){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryByWhere($vo, $where, $orderby);			$this->closeConnection($conn);			return $vos;		}		/**		 * get an array of Userstat instances by SQL		 * @param <Userstat> $vo		 * @param <string> $sql		 * @return Array<Userstat>		 */		public function getUserstatsBySQL($vo, $sql){			$conn = $this->getConnection();			$dao = new DAO();			$vos = $dao->queryBySQL($vo, $sql);			$this->closeConnection($conn);			return $vos;		}		/**		 * add a Userstat record to database		 * @param <Userstat> $vo		 * @return <Userstat> $vo		 */		public function addUserstat($vo){			$conn = $this->getConnection();			$dao = new DAO();			$vo = $dao->insert($vo);			$this->closeConnection($conn);			return $vo;		}		/**		 * update a batch of Userstat records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function updateUserstatBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->updateBySQL($sql);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Userstat records in database		 * @param <Userstat> $vo		 * @return <int> affected row number		 */		public function deleteUserstats($vo){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->batchDelete($vo);			$this->closeConnection($conn);			return $num;		}		/**		 * delete a batch of Userstat records in database		 * @param <string> $sql		 * @return <int> affected row number		 */		public function deleteUserstatsBySQL($sql){			$conn = $this->getConnection();			$dao = new DAO();			$num = $dao->deleteBySQL($sql);			$this->closeConnection($conn);			return $num;		}	}?>